$(function () {
    $(".side-nav .side-nav-menu li a").on("click", function () {
        $(this).parent().hasClass("open") ? $(this).parent().children(".dropdown-menu").slideUp(200, function () {
            $(this).parent().removeClass("open")
        }) : ($(this).parent().parent().children("li.open").children(".dropdown-menu").slideUp(200), $(this).parent().parent().children("li.open").children("a").removeClass("open"), $(this).parent().parent().children("li.open").removeClass("open"), $(this).parent().children(".dropdown-menu").slideDown(200, function () {
            $(this).parent().addClass("open")
        }))
    });
    $(".sidenav-fold-toggler").on("click", function (t) {
        $(".app").toggleClass("side-nav-folded");
        t.preventDefault()
    });
    $(".sidenav-expand-toggler").on("click", function (t) {
        $(".side-nav-backdrop").length ? $(".side-nav-backdrop").remove() : $(".app").append('<div class="side-nav-backdrop"><\/div>');
        $(".app").toggleClass("side-nav-expand");
        t.stopPropagation();
        $(".side-nav-backdrop").on("click", function () {
            $(".app").removeClass("side-nav-expand");
            $(this).remove()
        })
    });
    $(".scrollable").perfectScrollbar();
});